     ; throws an exception if the proof of work is invalid, or if the timestamp is too far from what it should be.
     ; This is <b>not</b> everything that is required for a block to be valid, only what is checkable independent
     ; of the chain and without a transaction index.
     ;
     ; @throws VerificationException
     ;;
    #_throws #_[ "VerificationException" ]
    #_method
    (defn #_"void" Block''verify-header [#_"Block" this]
        ;; Prove that this block is OK.  It might seem that we can just ignore most of these checks given that the
        ;; network is also verifying the blocks, but we cannot as it'd open us to a variety of obscure attacks.
        ;;
        ;; Firstly we need to ensure this block does in fact represent real work done.  If the difficulty is high
        ;; enough, it's probably been done by the network.
        (Block''check-proof-of-work this, true)
        (Block''check-timestamp this)
        nil
    )

    ;;;
     ; Checks the block contents.
     ;
     ; @param height Block height, if known, or -1 otherwise.
     ;               If valid, used to validate the coinbase input script of v2 and above blocks.
     ; @param flags Flags to indicate which tests should be applied (i.e. whether to test for height in the coinbase transaction).
     ; @throws VerificationException if there was an error verifying the block.
     ;;
    #_throws #_[ "VerificationException" ]
    #_method
    (defn #_"void" Block''verify-transactions [#_"Block" this, #_"int" height, #_"EnumSet<BlockVerifyFlag>" flags]
        ;; Now we need to check that the body of the block actually matches the headers.  The network won't generate
        ;; an invalid block, but if we didn't validate this then an untrusted man-in-the-middle could obtain the next
        ;; valid block from the network and simply replace the transactions in it with their own fictional
        ;; transactions that reference spent or non-existant inputs.
        (when (.isEmpty (:transactions this))
            (throw (VerificationException'new-1 "Block had no transactions"))
        )
        (when (< Block'MAX_BLOCK_SIZE (Block''get-optimal-encoding-message-size this))
            (throw (VerificationException'new-1 "Block larger than MAX_BLOCK_SIZE"))
        )
        (Block''check-transactions this, height, flags)
        (Block''check-merkle-root this)
        (Block''check-sig-ops this)
        (doseq [#_"Transaction" transaction (:transactions this)]
            (Transaction''verify-1 transaction)
        )
        nil
    )

    ;;;
     ; Verifies both the header and that the transactions hash to the merkle root.
     ;
     ; @param height Block height, if known, or -1 otherwise.
     ; @param flags Flags to indicate which tests should be applied (i.e. whether to test for height in the coinbase transaction).
     ; @throws VerificationException if there was an error verifying the block.
     ;;
    #_throws #_[ "VerificationException" ]
    #_method
    (defn #_"void" Block''verify-3 [#_"Block" this, #_"int" height, #_"EnumSet<BlockVerifyFlag>" flags]
        (Block''verify-header this)
        (Block''verify-transactions this, height, flags)
        nil
    )

    #_foreign
    #_override
    (defn #_"boolean" Object'''equals [#_"Block" this, #_"Object" o]
        (§ cond
            (= this o) true
            (or (nil? o) (not= (.getClass this) (.getClass o))) false
            :else (.equals (Message'''get-hash this), (Message'''get-hash (ß cast Block o)))
        )
    )

    #_foreign
    #_override
    (defn #_"int" Object'''hashCode [#_"Block" this]
        (.hashCode (Message'''get-hash this))
    )

    ;;;
     ; Returns the merkle root in big endian form, calculating it from transactions if necessary.
     ;;
    #_method
    (defn #_"Sha256Hash" Block''get-merkle-root [#_"Block" this]
        (when (nil? (:merkle-root this))
            ;; TODO: Check if this is really necessary.
            (Block''un-cache-header this)
            (§ assoc this :merkle-root (Block''calculate-merkle-root this))
