        (:merkle-root this)
    )

    ;;; Exists only for unit testing. ;;
    #_method
    (defn #_"void" Block''set-merkle-root [#_"Block" this, #_"Sha256Hash" value]
        (Block''un-cache-header this)
        (§ assoc this :merkle-root value)
        (§ assoc this :hash nil)
        nil
    )

    ;;; Adds a transaction to this block.  The nonce and merkle root are invalid after this. ;;
    #_method
    (defn #_"void" Block''add-transaction-2 [#_"Block" this, #_"Transaction" t]
        (Block''add-transaction-3 this, t, true)
        nil
    )

    ;;; Adds a transaction to this block, with or without checking the sanity of doing so. ;;
    #_method
    (defn #_"void" Block''add-transaction-3 [#_"Block" this, #_"Transaction" tx, #_"boolean" check?]
        (Block''un-cache-transactions this)
        (when (nil? (:transactions this))
            (§ assoc this :transactions (ß ArrayList.))
        )
        (ChildMessage''set-parent tx, this)
        (when (and check? (= (.size (:transactions this)) 0) (not (Transaction''is-coin-base tx)))
            (throw (RuntimeException. (str "Attempted to add a non-coinbase transaction as the first transaction: " tx)))
        )
        (when (and check? (< 0 (.size (:transactions this))) (Transaction''is-coin-base tx))
            (throw (RuntimeException. (str "Attempted to add a coinbase transaction when there already is one: " tx)))
        )
        (.add (:transactions this), tx)
        (Message'''adjust-length-3 this, (.size (:transactions this)), (:length tx))
        ;; Force a recalculation next time the values are needed.
        (§ assoc this :merkle-root nil)
        (§ assoc this :hash nil)
        nil
    )

    ;;;
     ; Returns the version of the block data structure as defined by the Bitcoin protocol.
     ;;
    #_method
    (defn #_"long" Block''get-version [#_"Block" this]
        (:version this)
    )

    ;;;
     ; Returns the hash of the previous block in the chain, as defined by the block header.
     ;;
    #_method
    (defn #_"Sha256Hash" Block''get-prev-block-hash [#_"Block" this]
        (:prev-block-hash this)
    )

    #_method
    (defn #_"void" Block''set-prev-block-hash [#_"Block" this, #_"Sha256Hash" __prevBlockHash]
        (Block''un-cache-header this)
        (§ assoc this :prev-block-hash __prevBlockHash)
        (§ assoc this :hash nil)
        nil
    )

    ;;;
     ; Returns the time at which the block was solved and broadcast, according to the clock of the solving node.
     ; This is measured in seconds since the UNIX epoch (midnight Jan 1st 1970).
     ;;
    #_method
    (defn #_"long" Block''get-time-seconds [#_"Block" this]
        (:time this)
    )

    ;;;
     ; Returns the time at which the block was solved and broadcast, according to the clock of the solving node.
     ;;
    #_method
    (defn #_"Date" Block''get-time [#_"Block" this]
        (ß Date. (* (Block''get-time-seconds this) 1000))
    )

    #_method
    (defn #_"void" Block''set-time [#_"Block" this, #_"long" time]
        (Block''un-cache-header this)
        (§ assoc this :time time)
        (§ assoc this :hash nil)
        nil
    )

    ;;;
     ; Returns the difficulty of the proof of work that this block should meet encoded <b>in compact form</b>.
     ; The {@link SPVBlockChain} verifies that this is not too easy by looking at the length of the chain when the block is added.
     ; To find the actual value the hash should be compared against, use {@link Block#getDifficultyTargetAsInteger()}.
     ; Note that this is <b>not</b> the same as the difficulty value reported by the Bitcoin "getdifficulty" RPC that you may see on various block explorers.
     ; That number is the result of applying a formula to the underlying difficulty to normalize the minimum to 1.
     ; Calculating the difficulty that way is currently unsupported.
     ;;
    #_method
    (defn #_"long" Block''get-difficulty-target [#_"Block" this]
        (:difficulty-target this)
    )

    ;;; Sets the difficulty target in compact form. ;;
    #_method
    (defn #_"void" Block''set-difficulty-target [#_"Block" this, #_"long" __compactForm]
        (Block''un-cache-header this)
        (§ assoc this :difficulty-target __compactForm)
        (§ assoc this :hash nil)
        nil
    )

    ;;;
     ; Returns the nonce, an arbitrary value that exists only to make the hash of the block header fall below the difficulty target.
     ;;
    #_method
    (defn #_"long" Block''get-nonce [#_"Block" this]
        (:nonce this)
    )

    ;;;
     ; Sets the nonce and clears any cached data.
     ;;
    #_method
    (defn #_"void" Block''set-nonce [#_"Block" this, #_"long" nonce]
        (Block''un-cache-header this)
        (§ assoc this :nonce nonce)
        (§ assoc this :hash nil)
        nil
    )

    ;;; Returns an immutable list of transactions held in this block, or null if this object represents just a header. ;;
    #_method
    (defn #_"List<Transaction>" Block''get-transactions [#_"Block" this]
        (when (some? (:transactions this)) (ß ImmutableList/copyOf (:transactions this)))
    )

    ;;;
     ; Return whether this block contains any transactions.
     ;
     ; @return true if the block contains transactions, false otherwise (is purely a header).
     ;;
    #_method
    (defn #_"boolean" Block''has-transactions [#_"Block" this]
        (not (.isEmpty (:transactions this)))
    )

    ;;;
     ; Returns whether this block conforms to
     ; <a href="https://github.com/bitcoin/bips/blob/master/bip-0034.mediawiki">BIP34: Height in Coinbase</a>.
     ;;
    #_method
    (defn #_"boolean" Block''is-bip34 [#_"Block" this]
