                    )
                )
                (catch IOException e
                    (throw (IllegalArgumentException. e))
                )
                (finally
                    (when (some? decoder)
                        (try
                            (.close decoder)
                            (catch IOException _
                            )
                        )
                    )
                )
            )
        )
