            (throw (MissingPrivateKeyException'new))
        )
    )

    #_protected
    #_method
    (defn #_"ECDSASignature" ECKey''do-sign [#_"ECKey" __, #_"Sha256Hash" input, #_"BigInteger" __privateKeyForSigning]
        (ensure some? __privateKeyForSigning)

