    #_protected
    #_throws #_[ "VerificationException", "BlockStoreException" ]
    #_override
    (defn #_"TransactionOutputChanges" BlockChain'''connect-transactions-3 [#_"FullPrunedBlockChain" this, #_"int" height, #_"Block" block]
        (assert-state (.isHeldByCurrentThread (:blockchain-lock this)))

        (when (nil? (:transactions block))
            (throw (RuntimeException. "connectTransactions called with Block that didn't have transactions!"))
        )
        (when-not (NetworkParameters''passes-checkpoint (:params this), height, (Message'''get-hash block))
            (throw (VerificationException'new-1 (str "Block failed checkpoint lockin at " height)))
        )

        (FullPrunedBlockStore'''begin-database-batch-write (:block-store this))

        (§ let [#_"LinkedList<UTXO>" __txOutsSpent (ß LinkedList.)
              #_"LinkedList<UTXO>" __txOutsCreated (ß LinkedList.)
              #_"long" __sigOps 0]

            (when (.isShutdown (:script-verification-executor this))
                (§ assoc this :script-verification-executor (ß Executors/newFixedThreadPool (.availableProcessors (Runtime/getRuntime))))
            )

            (let [#_"List<Future<VerificationException>>" __listScriptVerificationResults (ß ArrayList. (.size (:transactions block)))]
                (try
                    (when (not (NetworkParameters''is-checkpoint (:params this), height))
                        ;; BIP30 violator blocks are ones that contain a duplicated transaction.  They are all in the checkpoints list
                        ;; and we therefore only check non-checkpoints for duplicated transactions here.  See the BIP30 document
                        ;; for more details on this: https://github.com/bitcoin/bips/blob/master/bip-0030.mediawiki
                        (doseq [#_"Transaction" tx (:transactions block)]
                            (let [#_"Set<ScriptVerifyFlag>" __verifyFlags (NetworkParameters''get-transaction-verification-flags (:params this), block, tx, (BlockChain''get-version-tally this), height)
                                  #_"Sha256Hash" hash (Message'''get-hash tx)]
                                ;; If we already have unspent outputs for this hash, we saw the tx already.
                                ;; Either the block is being added twice (bug) or the block is a BIP30 violator.
                                (when (FullPrunedBlockStore'''has-unspent-outputs (:block-store this), hash, (.size (Transaction''get-outputs tx)))
                                    (throw (VerificationException'new-1 "Block failed BIP30 test!"))
                                )
                                ;; We already check non-BIP16 sigops in Block.verifyTransactions(true).
                                (when (.contains __verifyFlags, :ScriptVerifyFlag'P2SH)
                                    (§ ass __sigOps (+ __sigOps (Transaction''get-sig-op-count tx)))
                                )
                            )
                        )
                    )
                    (let [#_"Coin" __totalFees Coin'ZERO
                          #_"Coin" __coinbaseValue nil]
                        (doseq [#_"Transaction" tx (:transactions block)]
                            (let [#_"boolean" __isCoinBase (Transaction''is-coin-base tx)
                                  #_"Coin" __valueIn Coin'ZERO
                                  #_"Coin" __valueOut Coin'ZERO
                                  #_"List<Script>" __prevOutScripts (ß LinkedList.)
                                  #_"Set<ScriptVerifyFlag>" __verifyFlags (NetworkParameters''get-transaction-verification-flags (:params this), block, tx, (BlockChain''get-version-tally this), height)]
                                (when (not __isCoinBase)
                                    ;; For each input of the transaction remove the corresponding output from the set of unspent outputs.
                                    (loop-when-recur [#_"int" index 0] (< index (.size (Transaction''get-inputs tx))) [(inc index)]
                                        (let [#_"TransactionInput" in (.get (Transaction''get-inputs tx), index)
                                              #_"UTXO" __prevOut (FullPrunedBlockStore'''get-transaction-output (:block-store this), (Message'''get-hash (:outpoint in)), (:index (:outpoint in)))]
                                            (when (nil? __prevOut)
                                                (throw (VerificationException'new-1 "Attempted to spend a non-existent or already spent output!"))
                                            )
                                            ;; Coinbases can't be spent until they mature, to avoid re-orgs destroying entire transaction chains.
                                            ;; The assumption is there will ~never be re-orgs deeper than the spendable coinbase chain depth.
                                            (when (:coinbase? __prevOut)
                                                (when (< (- height (UTXO''get-height __prevOut)) (-> this :params :spendable-coinbase-depth))
                                                    (throw (VerificationException'new-1 (str "Tried to spend coinbase at depth " (- height (UTXO''get-height __prevOut)))))
                                                )
                                            )
                                            ;; TODO: Check we're not spending the genesis transaction here. Bitcoin Core won't allow it.
                                            (§ ass __valueIn (Coin''add __valueIn, (:value __prevOut)))
                                            (when (.contains __verifyFlags, :ScriptVerifyFlag'P2SH)
                                                (when (Script''is-pay-to-script-hash (:script __prevOut))
                                                    (§ ass __sigOps (+ __sigOps (Script'get-p2sh-sig-op-count (TransactionInput''get-script-bytes in))))
                                                )
                                                (when (< Block'MAX_BLOCK_SIGOPS __sigOps)
                                                    (throw (VerificationException'new-1 "Too many P2SH SigOps in block"))
                                                )
                                            )

                                            (.add __prevOutScripts, (:script __prevOut))
                                            (FullPrunedBlockStore'''remove-unspent-transaction-output (:block-store this), __prevOut)
                                            (.add __txOutsSpent, __prevOut)
                                        )
                                    )
                                )
                                (let [#_"Sha256Hash" hash (Message'''get-hash tx)]
                                    (doseq [#_"TransactionOutput" out (Transaction''get-outputs tx)]
                                        (§ ass __valueOut (Coin''add __valueOut, (TransactionOutput''get-value out)))
                                        ;; For each output, add it to the set of unspent outputs so it can be consumed in future.
                                        (let [#_"Script" script (FullPrunedBlockChain''get-script this, (TransactionOutput''get-script-bytes out))
                                              #_"UTXO" __newOut (UTXO'new-7 hash, (TransactionOutput'''get-index out), (TransactionOutput''get-value out), height, __isCoinBase, script, (FullPrunedBlockChain''get-script-address this, script))]
                                            (FullPrunedBlockStore'''add-unspent-transaction-output (:block-store this), __newOut)
                                            (.add __txOutsCreated, __newOut)
                                        )
                                    )
                                    ;; All values were already checked for being non-negative (as it is verified in Transaction.verify()),
                                    ;; but we check again here just for defence in depth.  Transactions with zero output value are OK.
                                    (when (or (< (Monetary'''signum __valueOut) 0) (< 0 (.compareTo __valueOut, (NetworkParameters''get-max-money (:params this)))))
                                        (throw (VerificationException'new-1 "Transaction output value out of range"))
                                    )

                                    (cond __isCoinBase
                                        (do
                                            (§ ass __coinbaseValue __valueOut)
                                        )
                                        :else
                                        (do
                                            (when (or (< (.compareTo __valueIn, __valueOut) 0) (< 0 (.compareTo __valueIn, (NetworkParameters''get-max-money (:params this)))))
                                                (throw (VerificationException'new-1 "Transaction input value out of range"))
                                            )
                                            (§ ass __totalFees (Coin''add __totalFees, (Coin''subtract __valueIn, __valueOut)))
                                        )
                                    )

                                    (when (and (not __isCoinBase) (:run-scripts this))
                                        ;; Because correctlySpends modifies transactions, this must come after we are done with tx.
                                        (let [#_"FutureTask<VerificationException>" future (ß FutureTask. (FullPrunedVerifier'new tx, __prevOutScripts, __verifyFlags))]
                                            (.execute (:script-verification-executor this), future)
                                            (.add __listScriptVerificationResults, future)
                                        )
                                    )
                                )
                            )
                        )
                        (when (or (< 0 (.compareTo __totalFees, (NetworkParameters''get-max-money (:params this)))) (< (.compareTo (Coin''add (Block''get-block-inflation block, height), __totalFees), __coinbaseValue) 0))
                            (throw (VerificationException'new-1 "Transaction fees out of range"))
                        )

                        (doseq [#_"Future<VerificationException>" future __listScriptVerificationResults]
                            (let [#_"VerificationException" e]
                                (try
                                    (§ ass e (.get future))
                                    (catch InterruptedException ie
                                        (throw (RuntimeException. ie)) ;; Shouldn't happen.
                                    )
                                    (catch ExecutionException ee
                                        (.error FullPrunedBlockChain'LOG, (str "Script.correctlySpends threw a non-normal exception: " (.getCause ee)))
                                        (throw (VerificationException'new-2 "Bug in Script.correctlySpends, likely script malformed in some new and interesting way.", ee))
                                    )
                                )
                                (when (some? e)
                                    (throw e)
                                )
                            )
                        )
                    )
                    (catch VerificationException e
                        (.shutdownNow (:script-verification-executor this))
                        (FullPrunedBlockStore'''abort-database-batch-write (:block-store this))
                        (throw e)
                    )
                    (catch BlockStoreException e
                        (.shutdownNow (:script-verification-executor this))
                        (FullPrunedBlockStore'''abort-database-batch-write (:block-store this))
                        (throw e)
                    )
                )

                (TransactionOutputChanges'new __txOutsCreated, __txOutsSpent)
            )
        )
    )

