        (assert-state (:v-running this))
        (§ assoc this :v-running false)
        (let [#_"ListenableFuture" future (.submit (:executor this),
                #_non-static
                (§ reify Runnable
                    #_foreign
                    #_override
                    (defn #_"void" Runnable'''run [#_"Runnable" this]
                        (§ try
                            (.info PeerGroup'LOG, "Stopping ...")
                            ;; Blocking close of all sockets.
