                            (PeerGroup''await-terminated (:channels (§ this PeerGroup)))
                            (doseq [#_"PeerDiscovery" __peerDiscovery (:peer-discoverers (§ this PeerGroup))]
                                (PeerDiscovery'''shutdown __peerDiscovery)
                            )
