        (§ sync (:peergroup-lock this)
            (let [#_"PeerAddress" localhost (PeerAddress'localhost (:params this))]
                (.put (:backoff-map this), localhost, (ExponentialBackoff'new (:peer-backoff-params this)))
                (PeerGroup''connect-to-4 this, localhost, true, (:v-connect-timeout-millis this))
            )
        )
    )

    ;;;
     ; Creates a version message to send, constructs a Peer object and attempts to connect it.
     ; Returns the peer on success or null on failure.
     ;
     ; @param address Remote network address.
     ; @param incrementMaxConnections Whether to consider this connection an attempt to fill our quota, or something explicitly requested.
     ; @return Peer or null.
     ;;
    #_protected
    #_method
    (defn #_"Peer" PeerGroup''connect-to-4 [#_"PeerGroup" this, #_"PeerAddress" address, #_"boolean" __incrementMaxConnections, #_"int" __connectTimeoutMillis]
        (assert-state (.isHeldByCurrentThread (:peergroup-lock this)))

        (§ let [#_"VersionMessage" ver (VersionMessage''duplicate (PeerGroup''get-version-message this))]
            (§ assoc ver :best-height (if (some? (:chain this)) (BlockChain''get-best-chain-height (:chain this)) 0))
            (§ assoc ver :time (Utils'current-time-seconds))

            (let [#_"Peer" peer (PeerGroup''create-peer this, address, ver)]
                (Peer''add-connected-event-listener-3 peer, Threading'SAME_THREAD, (:startup-listener this))
                (Peer''add-disconnected-event-listener-3 peer, Threading'SAME_THREAD, (:startup-listener this))
                (Peer''set-min-protocol-version peer, (:v-min-required-protocol-version this))
                (.add (:pending-peers this), peer)

                (try
                    (.info PeerGroup'LOG, "Attempting connection to {}     ({} connected, {} pending, {} max)", address, (.size (:peers this)), (.size (:pending-peers this)), (:max-connections this))
                    (let [#_"ListenableFuture<SocketAddress>" future (ClientConnectionManager'''open-connection (:channels this), (PeerAddress''to-socket-address address), peer)]
                        (when (.isDone future)
                            (ß Uninterruptibles/getUninterruptibly future)
                        )
                    )
                    (catch ExecutionException e
                        (let [#_"Throwable" cause (ß Throwables/getRootCause e)]
                            (.warn PeerGroup'LOG, (str "Failed to connect to " address ": " (.getMessage cause)))
                            (PeerGroup''handle-peer-death this, peer, cause)
                            (§ return nil)
                        )
                    )
                )
                (AbstractTimeoutHandler''set-socket-timeout peer, __connectTimeoutMillis)
                ;; When the channel has connected and version negotiated successfully, handleNewPeer will end up being called
                ;; on a worker thread.
                (when __incrementMaxConnections
                    ;; We don't use setMaxConnections here as that would trigger a recursive attempt to establish a new
                    ;; outbound connection.
                    (§ update this :max-connections inc)
                )
                peer
            )
        )
    )

    ;;; You can override this to customise the creation of {@link Peer} objects. ;;
    #_protected
    #_method
