    ;;;
     ; Returns the transaction hash as you see them in the block explorer.
     ;;
    #_override
    (defn #_"Sha256Hash" Message'''get-hash [#_"Transaction" this]
        (when (nil? (:hash this))
            (§ assoc this :hash (Sha256Hash'wrap-reversed (Sha256Hash'hash-twice-1 (Message''unsafe-bitcoin-serialize this))))
        )
        (:hash this)
    )

    ;;;
     ; Used by BitcoinSerializer.  The serializer has to calculate a hash for checksumming so to
     ; avoid wasting the considerable effort a set method is provided so the serializer can set it.
     ;
     ; No verification is performed on this hash.
     ;;
    #_method
    (defn #_"void" Transaction''set-hash [#_"Transaction" this, #_"Sha256Hash" hash]
        (§ assoc this :hash hash)
        nil
    )

    #_method
    (defn #_"String" Transaction''get-hash-as-string [#_"Transaction" this]
        (.toString (Message'''get-hash this))
    )

    ;;;
     ; Gets the sum of the inputs, regardless of who owns them.
     ;;
    #_method
    (defn #_"Coin" Transaction''get-input-sum [#_"Transaction" this]
        (§ let [#_"Coin" sum Coin'ZERO]
            (doseq [#_"TransactionInput" input (:inputs this)]
                (let [#_"Coin" value (TransactionInput''get-value input)]
                    (when (some? value)
                        (§ ass sum (Coin''add sum, value))
                    )
                )
            )
            sum
        )
    )

    ;;;
     ; Calculates the sum of the outputs that are sending coins to a key in the wallet.
     ;;
    #_method
    (defn #_"Coin" Transaction''get-value-sent-to-me [#_"Transaction" this, #_"TransactionBag" bag]
        ;; This is tested in WalletTest.
        (§ let [#_"Coin" sum Coin'ZERO]
            (doseq [#_"TransactionOutput" output (:outputs this)]
                (when (TransactionOutput''is-mine output, bag)
                    (§ ass sum (Coin''add sum, (TransactionOutput''get-value output)))
                )
            )
            sum
        )
    )

    ;;;
     ; Returns a map of block [hashes] which contain the transaction mapped to relativity counters, or null if this
     ; transaction doesn't have that data because it's not stored in the wallet or because it has never appeared in a
     ; block.
     ;;
    #_method
    (defn #_"Map<Sha256Hash, Integer>" Transaction''get-appears-in-hashes [#_"Transaction" this]
        (when (some? (:appears-in-hashes this)) (ß ImmutableMap/copyOf (:appears-in-hashes this)))
    )

    ;;;
     ; Convenience wrapper around getConfidence().getConfidenceType().
     ;
     ; @return true if this transaction hasn't been seen in any block yet.
     ;;
    #_method
    (defn #_"boolean" Transaction''is-pending [#_"Transaction" this]
        (= (TransactionConfidence''get-confidence-type (Transaction''get-confidence-t this)) :ConfidenceType'PENDING)
    )

    ;;;
     ; Puts the given block in the internal set of blocks in which this transaction appears.  This is
     ; used by the wallet to ensure transactions that appear on side chains are recorded properly even though
     ; the block stores do not save the transaction data at all.
     ;
     ; If there is a re-org this will be called once for each block that was previously seen, to update which block
     ; is the best chain.  The best chain block is guaranteed to be called last. So this must be idempotent.
     ;
     ; Sets updatedAt to be the earliest valid block time where this tx was seen.
     ;
     ; @param block     The {@link StoredBlock} in which the transaction has appeared.
     ; @param bestChain Whether to set the updatedAt timestamp from the block header (only if not already set).
     ; @param relativityOffset A number that disambiguates the order of transactions within a block.
     ;;
    #_method
    (defn #_"void" Transaction''set-block-appearance [#_"Transaction" this, #_"StoredBlock" block, #_"boolean" __bestChain, #_"int" __relativityOffset]
        (§ let [#_"long" __blockTime (* (Block''get-time-seconds (StoredBlock''get-header block)) 1000)]
            (when (and __bestChain (or (nil? (:updated-at this)) (= (.getTime (:updated-at this)) 0) (< __blockTime (.getTime (:updated-at this)))))
                (§ assoc this :updated-at (ß Date. __blockTime))
            )

            (Transaction''add-block-appearance this, (Message'''get-hash (StoredBlock''get-header block)), __relativityOffset)

            (when __bestChain
                (let [#_"TransactionConfidence" __transactionConfidence (Transaction''get-confidence-t this)]
                    ;; This sets type to BUILDING and depth to one.
                    (TransactionConfidence''set-appeared-at-chain-height __transactionConfidence, (StoredBlock''get-height block))
                )
            )
        )
        nil
    )

    #_method
    (defn #_"void" Transaction''add-block-appearance [#_"Transaction" this, #_"Sha256Hash" __blockHash, #_"int" __relativityOffset]
        ;; TODO: This could be a lot more memory efficient as we'll typically only store one element.
        (when (nil? (:appears-in-hashes this))
            (§ assoc this :appears-in-hashes (ß TreeMap.))
        )

        (.put (:appears-in-hashes this), __blockHash, __relativityOffset)
        nil
    )

    ;;;
     ; Calculates the sum of the inputs that are spending coins with keys in the wallet.  This requires the
     ; transactions sending coins to those keys to be in the wallet.  This method will not attempt to download
     ; the blocks containing the input transactions if the key is in the wallet but the transactions are not.
     ;
     ; @return sum of the inputs that are spending coins with keys in the wallet.
     ;;
    #_throws #_[ "ScriptException" ]
    #_method
    (defn #_"Coin" Transaction''get-value-sent-from-me [#_"Transaction" this, #_"TransactionBag" wallet]
        ;; This is tested in WalletTest.
        (§ let [#_"Coin" v Coin'ZERO]
            (doseq [#_"TransactionInput" input (:inputs this)]
                ;; This input is taking value from a transaction in our wallet.  To discover the value,
                ;; we must find the connected transaction.
                (let [#_"TransactionOutput" connected (TransactionInput''get-connected-output-2 input, (TransactionBag'''get-transaction-pool wallet, :PoolType'UNSPENT))]
                    (when (nil? connected)
                        (§ ass connected (TransactionInput''get-connected-output-2 input, (TransactionBag'''get-transaction-pool wallet, :PoolType'SPENT)))
                    )
                    (when (nil? connected)
                        (§ ass connected (TransactionInput''get-connected-output-2 input, (TransactionBag'''get-transaction-pool wallet, :PoolType'PENDING)))
                    )

                    ;; The connected output may be the change to the sender of a previous input sent to this wallet.
                    ;; In this case we ignore it.
                    (when (and (some? connected) (TransactionOutput''is-mine connected, wallet))
                        (§ ass v (Coin''add v, (TransactionOutput''get-value connected)))
                    )
                )
            )
            v
        )
    )

    ;;;
     ; Gets the sum of the outputs of the transaction.  If the outputs are less than the inputs, it does not count the fee.
     ;
     ; @return the sum of the outputs regardless of who owns them.
     ;;
    #_method
    (defn #_"Coin" Transaction''get-output-sum [#_"Transaction" this]
        (§ let [#_"Coin" sum Coin'ZERO]

            (doseq [#_"TransactionOutput" output (:outputs this)]
                (§ ass sum (Coin''add sum, (TransactionOutput''get-value output)))
            )

            sum
        )
    )

    ;;;
     ; Returns the difference of {@link Transaction#getValueSentToMe(TransactionBag)} and {@link Transaction#getValueSentFromMe(TransactionBag)}.
     ;;
    #_throws #_[ "ScriptException" ]
    #_method
    (defn #_"Coin" Transaction''get-value-2 [#_"Transaction" this, #_"TransactionBag" wallet]
        (Coin''subtract (Transaction''get-value-sent-to-me this, wallet), (Transaction''get-value-sent-from-me this, wallet))
    )

    ;;;
     ; The transaction fee is the difference of the value of all inputs and the value of all outputs.
     ; Currently, the fee can only be determined for transactions created by us.
     ;
     ; @return fee, or null if it cannot be determined.
     ;;
    #_method
    (defn #_"Coin" Transaction''get-fee [#_"Transaction" this]
        (when-not (or (.isEmpty (:inputs this)) (.isEmpty (:outputs this))) ;; Incomplete transaction.
            (§ let [#_"Coin" fee Coin'ZERO]
                (doseq [#_"TransactionInput" input (:inputs this)]
                    (when (nil? (TransactionInput''get-value input))
                        (§ return nil)
                    )
                    (§ ass fee (Coin''add fee, (TransactionInput''get-value input)))
                )
                (doseq [#_"TransactionOutput" output (:outputs this)]
                    (§ ass fee (Coin''subtract fee, (TransactionOutput''get-value output)))
                )
                fee
            )
        )
    )

    ;;;
     ; Returns true if any of the outputs is marked as spent.
     ;;
    #_method
    (defn #_"boolean" Transaction''is-any-output-spent [#_"Transaction" this]
        (not (every? #(TransactionOutput''is-available-for-spending %) (:outputs this)))
    )

    ;;;
     ; Returns false if this transaction has at least one output that is owned by the given wallet and unspent,
     ; true otherwise.
     ;;
    #_method
    (defn #_"boolean" Transaction''is-every-owned-output-spent [#_"Transaction" this, #_"TransactionBag" bag]
        (not (some #(and (TransactionOutput''is-available-for-spending %) (TransactionOutput''is-mine %, bag)) (:outputs this)))
    )

    ;;;
     ; Returns the earliest time at which the transaction was seen (broadcast or included into the chain),
     ; or the epoch if that information isn't available.
     ;;
    #_method
    (defn #_"Date" Transaction''get-update-time [#_"Transaction" this]
        ;; Older wallets did not store this field.  Set to the epoch.
        (when (nil? (:updated-at this))
            (§ assoc this :updated-at (ß Date. 0))
        )
        (:updated-at this)
    )

    #_method
    (defn #_"void" Transaction''set-update-time [#_"Transaction" this, #_"Date" time]
        (§ assoc this :updated-at time)
        nil
    )

    #_protected
    #_override
    (defn #_"void" Message'''un-cache [#_"Transaction" this]
        (Message'''un-cache (§ this super))
        (§ assoc this :hash nil)
        nil
    )

    #_protected
    (defn #_"int" Transaction'calc-length [#_"byte[]" buf, #_"int" offset]
        ;; jump past version (uint32)
        (§ let [#_"int" cursor (+ offset 4) #_"VarInt" varint (VarInt'new-2 buf, cursor) #_"long" __txInCount (:value varint)
              cursor (+ cursor (VarInt''get-original-size-in-bytes varint))]

            (loop-when-recur [#_"int" i 0] (< i __txInCount) [(inc i)]
                ;; 36 = length of previous_outpoint
                (§ ass cursor (+ cursor 36))
                (§ ass varint (VarInt'new-2 buf, cursor))
                (let [#_"long" __scriptLen (:value varint)]
                    ;; 4 = length of sequence field (unint32)
                    (§ ass cursor (+ cursor __scriptLen 4 (VarInt''get-original-size-in-bytes varint)))
                )
            )

            (let [varint (VarInt'new-2 buf, cursor) #_"long" __txOutCount (:value varint)
                  cursor (+ cursor (VarInt''get-original-size-in-bytes varint))]

                (loop-when-recur [#_"int" i 0] (< i __txOutCount) [(inc i)]
                    ;; 8 = length of tx value field (uint64)
                    (§ ass cursor (+ cursor 8))
                    (§ ass varint (VarInt'new-2 buf, cursor))
                    (let [#_"long" __scriptLen (:value varint)]
                        (§ ass cursor (+ cursor __scriptLen (VarInt''get-original-size-in-bytes varint)))
                    )
                )
                ;; 4 = length of lock_time field (uint32)
                (+ (- cursor offset) 4)
            )
        )
    )

    #_protected
    #_throws #_[ "ProtocolException" ]
    #_override
    (defn #_"void" Message'''parse-message [#_"Transaction" this]
        (§ assoc this :cursor (:offset this))
        (§ assoc this :version (Message''read-uint32 this))
        (§ assoc this :optimal-encoding-message-size 4)
        (let [#_"long" n (Message''read-var-int-1 this)]
            (§ update this :optimal-encoding-message-size + (VarInt'size-of n))
            (§ assoc this :inputs (ß ArrayList. (int n)))
            (loop-when-recur [#_"long" i 0] (< i n) [(inc i)]
                (let [#_"TransactionInput" input (TransactionInput'new-5i (:params this), this, (:payload this), (:cursor this), (:serializer this))]
                    (.add (:inputs this), input)
                    (let [#_"long" m (Message''read-var-int-2 this, TransactionOutPoint'MESSAGE_LENGTH)]
                        (§ update this :optimal-encoding-message-size + TransactionOutPoint'MESSAGE_LENGTH (VarInt'size-of m) m 4)
                        (§ update this :cursor + m 4)
                    )
                )
            )
        )
        (let [#_"long" n (Message''read-var-int-1 this)]
            (§ update this :optimal-encoding-message-size + (VarInt'size-of n))
            (§ assoc this :outputs (ß ArrayList. (int n)))
            (loop-when-recur [#_"long" i 0] (< i n) [(inc i)]
                (let [#_"TransactionOutput" output (TransactionOutput'new-5 (:params this), this, (:payload this), (:cursor this), (:serializer this))]
                    (.add (:outputs this), output)
                    (let [#_"long" m (Message''read-var-int-2 this, 8)]
                        (§ update this :optimal-encoding-message-size + 8 (VarInt'size-of m) m)
                        (§ update this :cursor + m)
                    )
                )
            )
        )
        (§ assoc this :lock-time (Message''read-uint32 this))
        (§ update this :optimal-encoding-message-size + 4)
        (§ assoc this :length (- (:cursor this) (:offset this)))
        nil
    )

    #_method
    (defn #_"int" Transaction''get-optimal-encoding-message-size [#_"Transaction" this]
        (if (= (:optimal-encoding-message-size this) 0)
            (§ assoc this :optimal-encoding-message-size (Message''get-message-size this))
        )
        (:optimal-encoding-message-size this)
    )

    ;;;
     ; The priority (coin age) calculation doesn't use the regular message size, but rather one adjusted downwards
     ; for the number of inputs.  The goal is to incentivise cleaning up the UTXO set with free transactions, if one
     ; can do so.
     ;;
    #_method
    (defn #_"int" Transaction''get-message-size-for-priority-calc [#_"Transaction" this]
        (§ let [#_"int" size (Message''get-message-size this)]
            (doseq [#_"TransactionInput" input (:inputs this)]
                ;; 41: min size of an input
                ;; 110: enough to cover a compressed pubkey p2sh redemption (somewhat arbitrary)
                (let [#_"int" benefit (+ 41 (min 110, (alength (Script''get-program (TransactionInput''get-script-sig input)))))]
                    (when (< benefit size)
                        (§ ass size (- size benefit))
                    )
                )
            )
            size
        )
    )

    ;;;
     ; A coinbase transaction is one that creates a new coin.  They are the first transaction in each block and their
     ; value is determined by a formula that all implementations of Bitcoin share.  In 2011 the value of a coinbase
     ; transaction is 50 coins, but in future it will be less.  A coinbase transaction is defined not only by its
     ; position in a block but by the data in the inputs.
     ;;
    #_method
    (defn #_"boolean" Transaction''is-coin-base [#_"Transaction" this]
        (and (= (.size (:inputs this)) 1) (TransactionInput''is-coin-base (.get (:inputs this), 0)))
    )

    ;;;
     ; A transaction is mature if it is either a building coinbase tx that is as deep or deeper than the required coinbase depth, or a non-coinbase tx.
     ;;
    #_method
    (defn #_"boolean" Transaction''is-mature [#_"Transaction" this]
        (§ cond
            (not (Transaction''is-coin-base this)) true
            (not= (TransactionConfidence''get-confidence-type (Transaction''get-confidence-t this)) :ConfidenceType'BUILDING) false
            :else (<= (-> this :params :spendable-coinbase-depth) (TransactionConfidence''get-depth-in-blocks (Transaction''get-confidence-t this)))
        )
    )

    #_foreign
    #_override
    (defn #_"String" Object'''toString [#_"Transaction" this]
        (Transaction''to-string-2 this, nil)
    )

    ;;;
     ; A human readable version of the transaction useful for debugging.  The format is not guaranteed to be stable.
     ;
     ; @param chain If provided, will be used to estimate lock times (if set).  Can be null.
     ;;
    #_method
    (defn #_"String" Transaction''to-string-2 [#_"Transaction" this, #_"BlockChain" chain]
        (§ let [#_"StringBuilder" sb (StringBuilder.)]
            (.. sb (append "  ") (append (Transaction''get-hash-as-string this)) (append "\n"))
            (when (some? (:updated-at this))
                (.. sb (append "  updated: ") (append (Utils'date-time-format-1-date (:updated-at this))) (append "\n"))
            )
            (when (not= (:version this) 1)
                (.. sb (append "  version ") (append (:version this)) (append "\n"))
            )
            (when (Transaction''is-time-locked this)
                (.. sb (append "  time locked until "))
                (cond (< (:lock-time this) Transaction'LOCKTIME_THRESHOLD)
                    (do
                        (.. sb (append "block ") (append (:lock-time this)))
                        (when (some? chain)
                            (.. sb (append " (estimated to be reached at ") (append (Utils'date-time-format-1-date (BlockChain''estimate-block-time chain, (int (:lock-time this))))) (append ")"))
                        )
                    )
                    :else
                    (do
                        (.. sb (append (Utils'date-time-format-1-time (* (:lock-time this) 1000))))
                    )
                )
                (.. sb (append "\n"))
            )
            (when (Transaction''is-opt-in-full-rbf this)
                (.. sb (append "  opts into full replace-by-fee\n"))
            )
            (when (Transaction''is-coin-base this)
                (let [#_"String" script
                      #_"String" script2]
                    (try
                        (§ ass script (.toString (TransactionInput''get-script-sig (.get (:inputs this), 0))))
                        (§ ass script2 (.toString (TransactionOutput''get-script-pub-key (.get (:outputs this), 0))))
                        (catch ScriptException _
                            (§ ass script "???")
                            (§ ass script2 "???")
                        )
                    )
                    (.. sb (append "     == COINBASE TXN (scriptSig ") (append script) (append ")  (scriptPubKey ") (append script2) (append ")\n"))
                    (§ return (.toString sb))
                )
            )

            (cond (not (.isEmpty (:inputs this)))
                (do
                    (doseq [#_"TransactionInput" in (:inputs this)]
                        (.. sb (append "     in   "))

                        (try
                            (let [#_"String" __scriptSigStr (.toString (TransactionInput''get-script-sig in))]
                                (.. sb (append (if (not (ß Strings/isNullOrEmpty __scriptSigStr)) __scriptSigStr "<no scriptSig>")))
                                (let [#_"Coin" value (TransactionInput''get-value in)]
                                    (when (some? value)
                                        (.. sb (append " ") (append (Coin''to-friendly-string value)))
                                    )
                                    (.. sb (append "\n          outpoint:"))
                                    (let [#_"TransactionOutPoint" outpoint (:outpoint in)]
                                        (.. sb (append outpoint))
                                        (let [#_"TransactionOutput" __connectedOutput (TransactionOutPoint''get-connected-output-1 outpoint)]
                                            (when (some? __connectedOutput)
                                                (let [#_"Script" __scriptPubKey (TransactionOutput''get-script-pub-key __connectedOutput)]
                                                    (when (or (Script''is-sent-to-address __scriptPubKey) (Script''is-pay-to-script-hash __scriptPubKey))
                                                        (.. sb (append " hash160:") (append (VarInt''encode Utils'HEX, (Script''get-pub-key-hash __scriptPubKey))))
                                                    )
                                                )
                                            )
                                            (when (TransactionInput''has-sequence in)
                                                (.. sb (append "\n          sequence:") (append (Long/toHexString (TransactionInput''get-sequence-number in))))
                                                (when (TransactionInput''is-opt-in-full-rbf in)
                                                    (.. sb (append ", opts into full RBF"))
                                                )
                                            )
                                        )
                                    )
                                )
                            )
                            (catch Exception e
                                (.. sb (append "[exception: ") (append (.getMessage e)) (append "]"))
                            )
                        )
                        (.. sb (append "\n"))
                    )
                )
                :else
                (do
                    (.. sb (append "     INCOMPLETE: No inputs!\n"))
                )
            )

            (doseq [#_"TransactionOutput" out (:outputs this)]
                (.. sb (append "     out  "))
                (try
                    (let [#_"String" __scriptPubKeyStr (.toString (TransactionOutput''get-script-pub-key out))]
                        (.. sb (append (if (not (ß Strings/isNullOrEmpty __scriptPubKeyStr)) __scriptPubKeyStr "<no scriptPubKey>")) (append " ") (append (Coin''to-friendly-string (TransactionOutput''get-value out))))
                        (when (not (TransactionOutput''is-available-for-spending out))
                            (.. sb (append " Spent"))
                        )
                        (let [#_"TransactionInput" __spentBy (TransactionOutput''get-spent-by out)]
                            (when (some? __spentBy)
