        (.write baos, (VarInt''encode (VarInt'new-1 (alength (:script-bytes this)))))
        (.write baos, (:script-bytes this))
        (Utils'uint32-to-byte-stream-le (:sequence this), baos)
        nil
    )

    ;;;
