    #_method
    (defn #_"byte[]" TransactionInput''get-script-bytes [#_"TransactionInput" this]
        (:script-bytes this)
    )

    ;;; Clear input scripts, e.g. in preparation for signing. ;;
    #_method
    (defn #_"void" TransactionInput''clear-script-bytes [#_"TransactionInput" this]
        (TransactionInput''set-script-bytes this, TransactionInput'EMPTY_ARRAY)
        nil
    )

    ;;;
     ; @param scriptBytes The scriptBytes to set.
     ;;
    #_method
    (defn #_"void" TransactionInput''set-script-bytes [#_"TransactionInput" this, #_"byte[]" script]
        (Message'''un-cache this)
        (§ assoc this :script-sig nil)
        (let [#_"int" __oldLength (:length this)]
            (§ assoc this :script-bytes script)
            ;; 40 = previous_outpoint (36) + sequence (4)
            (let [#_"int" __newLength (+ 40 (if (some? script) (+ (VarInt'size-of (alength script)) (alength script)) 1))]
                (ChildMessage''adjust-length-2 this, (- __newLength __oldLength))
