    #_method
    (defn #_"TransactionOutput" TransactionInput''get-connected-output-2 [#_"TransactionInput" this, #_"Map<Sha256Hash, Transaction>" transactions]
        (§ let [#_"Transaction" tx (.get transactions, (Message'''get-hash (:outpoint this)))]
