;;;
 ; Utility class that holds all the registered NetworkParameters types used for Address auto discovery.
 ; By default only MainNetParams and TestNetParams are used.
 ;;
#_stateless
(defclass Networks
    ;;; Registered networks. ;;
    (§ def- #_"Set<NetworkParameters>" Networks'NETWORKS (ß ImmutableSet/of TestNetParams'INSTANCE, MainNetParams'INSTANCE))
)

;;;
 ; Parameters for the testnet, a separate public instance of Bitcoin that has relaxed rules suitable for development
 ; and testing of applications and new Bitcoin versions.
 ;;
(defclass TestNetParams (§ extends NetworkParameters)
    (defn #_"TestNetParams" TestNetParams'new []
        (§ let [this (merge (NetworkParameters'new) {})]

            (§ assoc this :id NetworkParameters'ID_TESTNET)
            (§ assoc this :packet-magic 0x0b110907)
            (§ assoc this :interval NetworkParameters'INTERVAL)
            (§ assoc this :target-timespan NetworkParameters'TARGET_TIMESPAN)
            (§ assoc this :max-target (Utils'decode-compact-bits 0x1d00ffff))
            (§ assoc this :port 18333)
            (§ assoc this :address-header 111)
            (§ assoc this :p2sh-header 196)
            (§ assoc this :acceptable-address-codes (int-array [ (:address-header this), (:p2sh-header this) ]))
            (Block''set-time (:genesis-block this), 1296688602)
            (Block''set-difficulty-target (:genesis-block this), 0x1d00ffff)
            (Block''set-nonce (:genesis-block this), 414098458)
            (§ assoc this :spendable-coinbase-depth 100)
            (§ assoc this :subsidy-decrease-block-count 210000)
            (assert-state (= (Block''get-hash-as-string (:genesis-block this)) "000000000933ea01ad0ee984209779baaec3ced90fa3f408719526f8d77f4943"))
            (§ assoc this :alert-signing-key (.decode Utils'HEX, "04302390343f91cc401d56d68b123028bf52e5fca1939df127f63c6467cdf9c8e2c14b61104cf817d0b780da337893ecc4aaff1309e536162dabbdb45200ca2b0a"))

            (§ assoc this :dns-seeds (into-array String
            [
                "testnet-seed.bitcoin.jonasschnelli.ch", ;; Jonas Schnelli
                "testnet-seed.bluematt.me",              ;; Matt Corallo
                "testnet-seed.bitcoin.petertodd.org",    ;; Peter Todd
                "testnet-seed.bitcoin.schildbach.de",    ;; Andreas Schildbach
            ]))
            (§ assoc this :addr-seeds nil)
            (§ assoc this :bip32-header-pub 0x043587cf)
            (§ assoc this :bip32-header-priv 0x04358394)

            (§ assoc this :majority-enforce-block-upgrade 51)
            (§ assoc this :majority-reject-block-outdated 75)
            (§ assoc this :majority-window 100)

            (§ assoc this :textual-checkpoints TestNetParams'TEXTUAL_CHECKPOINTS)
            this
        )
    )

    (§ def- #_"TestNetParams" TestNetParams'INSTANCE (TestNetParams'new))

    (§ def- #_"Date" TestNetParams'TESTNET_DIFF_DATE (ß Date. 1329264000000)) ;; February 16th 2012

    #_throws #_[ "VerificationException", "BlockStoreException" ]
    #_override
    (defn #_"void" NetworkParameters'''check-difficulty-transitions [#_"TestNetParams" this, #_"StoredBlock" __storedPrev, #_"Block" __nextBlock, #_"BlockStore" __blockStore]
        (§ cond (and (not (NetworkParameters''is-difficulty-transition-point this, (StoredBlock''get-height __storedPrev))) (.after (Block''get-time __nextBlock), TestNetParams'TESTNET_DIFF_DATE))
            (do
                (let [#_"Block" prev (StoredBlock''get-header __storedPrev)]

                    ;; After 15th February 2012 the rules on the testnet change to avoid people running up the difficulty
                    ;; and then leaving, making it too hard to mine a block.  On non-difficulty transition points, easy
                    ;; blocks are allowed if there has been a span of 20 minutes without one.
                    (let [#_"long" __timeDelta (- (Block''get-time-seconds __nextBlock) (Block''get-time-seconds prev))]
                        ;; There is an integer underflow bug in bitcoin-qt that means mindiff blocks are accepted when time
                        ;; goes backwards.
                        (when (and (<= 0 __timeDelta) (<= __timeDelta (* 2 NetworkParameters'TARGET_SPACING)))
                            ;; Walk backwards until we find a block that doesn't have the easiest proof of work, then check
                            ;; that difficulty is equal to that one.
                            (let [#_"StoredBlock" cursor __storedPrev]
                                (loop-when-recur [] (and (not (.equals (StoredBlock''get-header cursor), (:genesis-block this))) (not= (rem (StoredBlock''get-height cursor) (:interval this)) 0) (.equals (Block''get-difficulty-target-as-integer (StoredBlock''get-header cursor)), (:max-target this))) []
                                    (§ ass cursor (StoredBlock''get-prev cursor, __blockStore))
                                )
                                (let [#_"BigInteger" __cursorTarget (Block''get-difficulty-target-as-integer (StoredBlock''get-header cursor))
                                      #_"BigInteger" __newTarget (Block''get-difficulty-target-as-integer __nextBlock)]
                                    (when-not (.equals __cursorTarget, __newTarget)
                                        (throw (VerificationException'new-1 (str "Testnet block transition that is not allowed: " (Long/toHexString (Block''get-difficulty-target (StoredBlock''get-header cursor))) " vs " (Long/toHexString (Block''get-difficulty-target __nextBlock)))))
                                    )
                                )
                            )
                        )
                    )
                )
            )
            :else
            (do
                (NetworkParameters'''check-difficulty-transitions (§ this super), __storedPrev, __nextBlock, __blockStore)
            )
        )
        nil
    )

