                            (do
                                (condp =? opcode
                                    ScriptOpCodes'OP_IF
                                        (do
                                            (when (not __shouldExecute)
                                                (.add __ifStack, false)
                                                (§ continue )
                                            )
                                            (when (< (.size stack) 1)
