    (§ def #_"MonetaryFormat" MonetaryFormat'UBTC (MonetaryFormat''optional-decimals (MonetaryFormat''min-decimals (MonetaryFormat''shift (MonetaryFormat'new-0), 6), 0), 2))
    ;;; Standard format for fiat amounts. ;;
