        #_"char" :positive-sign 0
        #_"char" :zero-digit 0
        #_"char" :decimal-mark 0
        #_"int" :min-decimals 0
        #_"List<Integer>" :decimal-groups nil
        #_"int" :shift 0
        #_"RoundingMode" :rounding-mode nil
        #_"String[]" :codes nil
        #_"char" :code-separator 0
        #_"boolean" :code-prefixed false
