    #_throws #_[ "VerificationException" ]
    #_method
    (defn- #_"void" Wallet''receive [#_"Wallet" this, #_"Transaction" tx, #_"StoredBlock" block, #_"NewBlockType" __blockType, #_"int" __relativityOffset]
        ;; Runs in a peer thread.
